import React, { useState } from 'react';
import "./reservations.css";


const Reservations = () => {
  /*Step1: Create Local State*/
  /*setName("") is to clear out the input*/
  const [name, setName] = useState("")
  const [date, setDate] = useState("")
  const [guests, setGuests] = useState("")



  const handleSubmit = (e) => {
    e.preventDefault();
    setName("");
    setDate("");
    setGuests("");
    document.getElementById("res-time").setAttribute("value", "17:00");
    document.getElementById("occasion").setAttribute("value", "Birthday");
    console.log("Form submitted");
  }

  const handleNameChange = (e) => {
    setName(e.target.value)
  }

  const handleDateChange = (e) => {
    setDate(e.target.value)
  }

  const handleGuestsChange = (e) => {
    setGuests(e.target.value)
  }

  /* Step 2: Hook Up the text input via two props on Line 16 adding value and onChange*/
  /* Step 3: to control the submission of the form, use onSubmit prop*/

  return (
    <div className="reservation-field" >
      <form onSubmit={handleSubmit}>
        <fieldset>
          <div className="reservation-name">
            <label htmlFor="name">Name:</label>
            <input
              id="name"
              type="text"
              placeholder="Name"
              name="name"
              value={name}
              onChange={handleNameChange}
            />
            <label htmlFor="res-date">Choose date:</label>
            <input
              type="date"
              id="res-date"
              placeholder="Choose a date"
              value={date}
              onChange={handleDateChange}
              required
            />
            <label htmlFor="res-time">Choose time:</label>
            <select id="res-time" defaultValue="17:00">
              <option value="17:00">5:00 PM</option>
              <option value="18:00">6:00 PM</option>
              <option value="19:00">7:00 PM</option>
              <option value="20:00">8:00 PM</option>
              <option value="21:00">9:00 PM</option>
              <option value="22:00">10:00 PM</option>
            </select>
            <label htmlFor="guests">Number of guests:</label>
            <input
              type="number"
              placeholder="1"
              min="1"
              max="10"
              id="guests"
              value={guests}
              onChange={handleGuestsChange}
              required
            />
            <label htmlFor="occasion">Occasion:</label>
            <select id="occasion" defaultValue="Birthday">
              <option>Birthday</option>
              <option>Anniversary</option>
            </select>
          </div>
          <button disabled={!name} type="submit">Submit</button>
        </fieldset>
      </form>
    </div>

  )
}

export default Reservations;
