import React, { useState } from 'react';



const Main = () => {
  const [expanded, setExpanded] = useState(false);

  const handleExpand = () => {
    setExpanded(!expanded);
  };
  
  
  
  return (
    <main>
      <div className="hero-container">
        <div className="hero-content">
          <h1>Welcome to the Little Lemon Restaurant</h1>
          <div className={`hero-content-text ${expanded ? 'expanded' : ''}`}>
            <p>Little Lemon is a Mediterranean restaurant that offers a variety of delicious and healthy dishes.<br/> 
            Our menu is inspired by the flavors and ingredients of the Mediterranean region,<br/>
            and we use only the freshest ingredients in all of our dishes.</p>
            <p>Learn more about our menu and our commitment to using locally sourced, organic ingredients.<br/>
            Whether you're a vegetarian, vegan, or just looking for a healthy and delicious meal, Little Lemon has something for everyone.<br/>
            Come visit us and experience the flavors of the Mediterranean for yourself!</p>
            {!expanded && <a href="#" onClick={handleExpand}>Read more</a>}
          </div>
          {expanded && <a href="#" onClick={handleExpand}>Show less</a>}
          <a href=".com" className="reserve-table-button">Reserve Table</a>
        </div>
        <div className="hero-image">
          <img src="https://www.todaystraveller.net/wp-content/uploads/2021/11/Sizzler-768x768.jpg" alt="Sizzling food"/>
        </div>
      </div>
    <article>
      <h2>Highlights</h2>
    </article>
    <article>
      <h2>Testimonials</h2>
    </article>
    <article>
      <h2>About</h2>
    </article>
  </main>
  )
}

export default Main;
