import React from 'react';
import "./menu.css";


const Menu = () => {
  return (
    // <div className="container-menu">
    //   <div>
    //     <img src="https://www.thechunkychef.com/wp-content/uploads/2021/07/Greek-Salad-Recipe-recipe-card-680x680.jpg" alt="Greek Salad"/>
    //     <div className="food-name">
    //     <h2>Greek Salad</h2>
    //     </div>
    //     <p>delicacy of mediterranean food</p>
    //     <p>Price: $5.99</p>
    //     <button className="order-button-menu">Order</button>
    //   </div>
    //   <div>Pizza Margherita</div>
    //   <div>Moroccan Tagine</div>
    //   <div>Moussaka</div>
    //   <div>Bruschetta</div>
    //   <div>Tabouli</div>
    //   <div>Falafel</div>
    //   <div>Lemon Meringue Tart</div>
    // </div>
  
    <form class="menu-order">
    <h2>Menu Order Form</h2>
    <label for="name">Name:</label>
    <input type="text" id="name" name="name" required/>
    <label for="email">Email:</label>
    <input type="email" id="email" name="email" required/>
    <label for="phone">Phone:</label>
    <input type="tel" id="phone" name="phone" required/>
    <label for="menu-item">Menu Item:</label>
    <select id="menu-item" name="menu-item" required>
      <option value="">--Select--</option>
      <option value="Greek Salad">Greek Salad</option>
      <option value="Margherita Pizza">Margherita Pizza</option>
      <option value="Pad Thai">Pad Thai</option>
    </select>
    <label for="quantity">Quantity:</label>
    <input type="number" id="quantity" name="quantity" min="1" max="10" required/>
    <button type="submit">Submit</button>
  </form>

    )
}

export default Menu;
