import React, { useState } from 'react';

// const Main = () => {
//   const [showFullText, setShowFullText] = useState(false);

const TextToggle = ({ initialText, fullText }) => {
  const [showFullText, setShowFullText] = useState(false);

  const toggleText = () => {
    setShowFullText(!showFullText);
  };
  // const textToShow = showFullText ? (
  //   <>
  //     Little Lemon is a Mediterranean restaurant that offers a variety of delicious and healthy dishes.
  //     Our menu is inspired by the flavors and ingredients of the Mediterranean region,
  //     and we use only the freshest ingredients in all of our dishes.
  //     Learn more about our menu and our commitment to using locally sourced, organic ingredients.
  //     Whether you're a vegetarian, vegan, or just looking for a healthy and delicious meal, Little Lemon has something for everyone.
  //     Come visit us and experience the flavors of the Mediterranean for yourself!
  //   </>
  // ) : (
  //   <>
  //     Little Lemon is a Mediterranean restaurant that offers a variety of delicious and healthy dishes.
  //     Our menu is inspired by the flavors and ingredients of the Mediterranean region,
  //     and we use only the freshest ingredients in all of our dishes.
  //   </>
  // );
  return (
    <p className="description" onClick={toggleText}>
      {showFullText ? fullText : initialText}
      {!showFullText && <span>...</span>}
    </p>
  );
};

const Main = () => {
  return (
    // <main>
    //   <div className="hero-container">
    //     <div className="hero-content">
    //       <h1>Welcome to the Little Lemon Restaurant!</h1>
    //       <div onClick={toggleText}>
    //         <p class="hero-paragraph">
    //           {textToShow}
    //           {!showFullText && <span>...</span>}
    //         </p>
    //         <a href=".com" className="reserve-table-button">RESERVE TABLE NOW!</a>
    //       </div>
    //     </div>
    //     <div className="hero-image">
    //       <img src="https://www.todaystraveller.net/wp-content/uploads/2021/11/Sizzler-768x768.jpg" alt="Sizzling Mediterranean food" />
    //     </div>
    //   </div>
    <main>
      <div className="hero-container">
        <div className="hero-content">
          <h1>Welcome to the Little Lemon Restaurant!</h1>
          <TextToggle
            initialText="Little Lemon is a Mediterranean restaurant that offers a variety of delicious and healthy dishes.
              Our menu is inspired by the flavors and ingredients of the Mediterranean region,
              and we use only the freshest ingredients in all of our dishes."
            fullText="Little Lemon is a Mediterranean restaurant that offers a variety of delicious and healthy dishes.
              Our menu is inspired by the flavors and ingredients of the Mediterranean region,
              and we use only the freshest ingredients in all of our dishes.
              Learn more about our menu and our commitment to using locally sourced, organic ingredients.
              Whether you're a vegetarian, vegan, or just looking for a healthy and delicious meal, Little Lemon has something for everyone.
              Come visit us and experience the flavors of the Mediterranean for yourself!"
          />
          <a href=".com" className="reserve-table-button">RESERVE TABLE NOW!</a>
        </div>
        <div className="hero-image">
          <img src="https://www.todaystraveller.net/wp-content/uploads/2021/11/Sizzler-768x768.jpg" alt="Sizzling Mediterranean food" />
        </div>
      </div>

      <article>
        <div className="specials-container">
        <div className="special-of-the-week">
          <h2>Special of the Week</h2>
        </div>
          <div className="specials-content">
            <div class="greek-salad">
              <h3>Greek Salad</h3>
              <p class="price-salad">$12.99</p>
              <div className="salad-image">
                <img src="https://cdn.loveandlemons.com/wp-content/uploads/2019/07/greek-salad-2-786x1024.jpg" alt="Greek Salad" />
              </div>
              <p class="description-salad" onClick={toggleText}>
                {showFullText ? (
                  <>
                    Enjoy the freshness of a classic Greek Salad made with crisp lettuce, juicy tomatoes, tangy feta cheese, and kalamata olives, drizzled with a zesty lemon and olive oil dressing.
                    Perfect to share with friends and family.
                  </>
                ) : (
                  <>
                    Enjoy the freshness of a classic Greek Salad made with crisp lettuce, juicy tomatoes, tangy feta cheese, and kalamata olives...
                  </>
                )}
              </p>
              <button class="order-btn1">Order a delivery</button>
            </div>

            <div class="bruschetta">
              <h3>Bruschetta</h3>
              <p class="price-bruschetta">$7.99</p>
              <div className="bruschetta-image">
                <img src="https://kitchendivas.com/wp-content/uploads/bruschetta-with-cheese-3-768x1152.jpg" alt="bruschetta with bread" />
              </div>
              <p class="description-bruschetta" onClick={toggleText}>
              {showFullText ? (
                  <>
                    Indulge in our delicious bruschetta, a traditional Italian antipasto consisting of grilled bread rubbed with garlic and topped with juicy diced tomatoes, fresh basil, and a drizzle of olive oil.
                     The perfect appetizer to share or as a light meal on its own.
                  </>
                ) : (
                  <>
                    Indulge in our delicious bruschetta, a traditional Italian antipasto consisting of grilled bread rubbed with garlic...
                  </>
                )}
                </p>
              <button class="order-btn2">Order a delivery</button>
            </div>

            <div class="lemon-dessert">
              <h3>Lemon Meringue Tart</h3>
              <p class="price-dessert">$5.99</p>
              <div className="dessert-image">
                <img src="https://www.homecookingadventure.com/wp-content/uploads/2022/01/lemon_meringue_tart_main.jpg" alt="bruschetta with bread" />
              </div>
              <p class="description-dessert" onClick={toggleText}>
              {showFullText ? (
                  <>
                    Taste the perfect balance of sweet and tangy flavors with our lemon meringue tart. Made with a buttery, flaky crust and filled with smooth and zesty lemon curd, this dessert is topped with a fluffy and lightly toasted meringue that adds a delightful texture to each bite. 
                    Perfect for any occasion, our lemon meringue tart is sure to leave a lasting impression on your taste buds.
                  </>
                ) : (
                  <>
                   Taste the perfect balance of sweet and tangy flavors with our lemon meringue tart. Made with a buttery, flaky crust and filled with...
                  </>
                )}
                </p>
              <button class="order-btn3">Order a delivery</button>
            </div>
          </div>
        </div>
      </article>

      <article>
        <h2>Testimonials</h2>
      </article>
      <article>
        <h2>About</h2>
      </article>
    </main>
  )
}

export default Main;
